    private void boton_acepActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_boton_acepActionPerformed
        
        double gast = Double.parseDouble(campo_gasto.getText());
        int tip = Integer.parseInt(campo_tipo.getText());
        int sex = Integer.parseInt(campo_sexo.getText());
        String nom = campo_nomb.getText();
        Seguro paciente = new Seguro(nom,tip,sex,gast);
        textarea.append(paciente.toString());
        textarea.append("\n=======\n");
        campo_gasto.setText("");  campo_nomb.setText("");
    }//GEN-LAST:event_boton_acepActionPerformed

    private void boton_totActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_boton_totActionPerformed
        textarea.append("\n Total Descuentos: "+Seguro.sumaDesc);
        textarea.append("\n Pagos Totales: "+Seguro.sumaPagosTot);
        
    }//GEN-LAST:event_boton_totActionPerformed

